CustomField.formula:
/* for a job that runs once a day (every 1440 mins), with an update window of 3 hours (180) */
IF(
   (ROUND(((NOW() - LastModifiedDate)*1440),1) > 1440 + 180), /* Over the update window */
    'Unable to update this rate due to data integrity', 
    IF(
       NOT(ISBLANK(Error_Message__c)),
       Error_Message__c,
       'Up to date'
    )
)
